with open("input.txt", "r") as f: print(sum([100 * sum([i + 1 for i in range(len(area) - 1) if sum(sum(l != r for l, r in zip(r1, r2)) for r1, r2 in zip(area[i::-1], area[i+1:])) == 1]) + sum([i + 1 for i in range(len(list(map(''.join, zip(*area[::-1])))) - 1) if sum(sum(l != r for l, r in zip(r1, r2)) for r1, r2 in zip(list(map(''.join, zip(*area[::-1])))[i::-1], list(map(''.join, zip(*area[::-1])))[i+1:])) == 1]) for area in [[x for x in area.splitlines()] for area in f.read().split("\n\n")]]))